import unittestfrom read_animals import generate_summaryimport jsonclass test_read_animals(unittest.TestCase):        def test_summary_stats(self):        with open('animals.json', 'r') as f:            data = json.load(f)                animals = data['animals']        stats = generate_summary(animals)        self.assertEqual(type(stats), dict)        self.assertEqual(len(stats), 4)        self.assertEqual(type(stats['head_count']), dict)        self.assertLessEqual(stats['avg_arms'], 10.05)        self.assertLessEqual(stats['avg_legs'], 12.05)        self.assertEqual(stats['avg_arms'] + stats['avg_legs'], stats['avg_tails'])        if __name__ == '__main__':    unittest.main()