from flask import Flaskimport flaskimport jsondef get_data():    with open('animals.json', 'r') as file:        data = json.load(file)        return dataapp = Flask(__name__)@app.route('/animals', methods=['GET'])def return_data():    return flask.jsonify(get_data())@app.route('/animals/head/<string:head>', methods=['GET'])def return_head(head):    output =[];    animals = get_data()['animals']        for animal in animals:        if animal['head'] == head:            output.append(animal)        return flask.jsonify(output)@app.route('/animals/legs/<int:num>', methods=['GET'])def return_legs(num):    output = [];    animals = get_data()['animals']        for animal in animals:        if animal['legs'] == num:            output.append(animal)                return flask.jsonify(output)if __name__ == '__main__':    app.run(debug='True', host='0.0.0.0')